import{mostrarEmergente,disabled}from"./funciones-min.js";import{btnEnviar}from"./variables-min.js";const urlInformacion="http://localhost:4000/informacion",urlContactos="http://localhost:4000/contactos";export const obtenerInformacion=async()=>{try{const o=await fetch(urlInformacion);return await o.json()}catch(o){console.log(o),mostrarEmergente("database","La pagina no se a podido conectar con la API<br/>¡inténtalo más tarde!",4e3)}};export const nuevoContacto=async o=>{try{disabled(btnEnviar,4500),await fetch(urlContactos,{method:"POST",body:JSON.stringify(o),headers:{"Content-Type":"application/json"}}),mostrarEmergente("send","¡Enviado correctamente!",2e3)}catch(o){console.log(o),mostrarEmergente("database","La pagina no se a podido conectar con la API<br/>¡inténtalo más tarde!",4e3)}};